settings.customcssrules:
#comments {
    height: 100vh;
    overflow-y: scroll;
}
#comments > ytd-item-section-renderer:nth-child(2) > div:nth-child(1) {
    position: sticky;
    top: 0px;
    background: var(--yt-spec-general-background-a) !important;
    z-index: 2;
}

settings.customscript:
function handleChange() {
    const primary_inner = document.getElementById('primary-inner');
    const secondary_inner = document.getElementById('secondary-inner');
    const secondary = document.getElementById('secondary');
    const comments = document.getElementById('comments');
    primary_inner.appendChild(secondary_inner);
    secondary.appendChild(comments);
}

var observer = new MutationObserver(function (mutations, me) {
    console.warn('Observing if comments generated');
    var p = document.getElementById('primary-inner');
    var c = document.getElementById('comments');
    if (c && p) {
        handleChange();
        console.warn('Observing completed');
        me.disconnect();
        return;
    }
});

observer.observe(document, {
    childList: true,
    subtree: true
});
